const ArrayUtils = {
  createRange(start, end, step = 1) {
    if (start > end) {
      return [];
    }
    const result = [];
    for (let i = start; i <= end; i += step) {
      result.push(i);
    }
    return result;
    /* Alternative with Array.from:
    const length = Math.floor((end - start) / step) + 1;
    if (length <= 0) return [];
    return Array.from({ length }, (_, i) => start + (i * step));
    */
  },

  getUniqueElements(arr) {
    // Handles non-array inputs or ensures it's an array for Set constructor
    if (!Array.isArray(arr)) {
      return [];
    }
    return [...new Set(arr)];
  },

  findMax(arr) {
    if (!Array.isArray(arr) || arr.length === 0) {
      return null;
    }
    // Check if all elements are numbers. If not, return null.
    if (arr.some(isNaN) || arr.some(item => typeof item !== 'number')) {
      return null; 
    }
    return Math.max(...arr);
  },

  hasAnyCommonElement(arr1, arr2) {
    if (!Array.isArray(arr1) || !Array.isArray(arr2) || arr1.length === 0 || arr2.length === 0) {
      return false; // No common elements if either array is empty or not an array
    }
    const set1 = new Set(arr1);
    return arr2.some(item => set1.has(item));
  }
};

