      const value = item[key];
      if (!currentGroup[value]) {
        currentGroup[value] = (i === keys.length - 1) ? [] : {};
      }
      currentGroup = currentGroup[value];
    }
    currentGroup.push(item);
    return result;
  }, {});
}

