function advancedGroupBy(array, keyExtractor) {
  if (!Array.isArray(array)) {
    throw new Error('Input must be an array.');
  }

  return array.reduce((acc, item) => {
    if (item === null || typeof item === 'undefined') {
      // Optionally handle null/undefined items, e.g., skip them or group under a special key
      return acc;
    }

    let key;
    if (typeof keyExtractor === 'string') {
      key = item[keyExtractor];
    } else if (typeof keyExtractor === 'function') {
      key = keyExtractor(item);
    } else {
      throw new Error('keyExtractor must be a string or a function.');
    }

    // Handle cases where the extracted key itself might be null or undefined
    const groupKey = (key === null || typeof key === 'undefined') ? 'null_undefined_group' : key;

    (acc[groupKey] = acc[groupKey] || []).push(item);
    return acc;
  }, {});
}

