function mockFetch(url, delay, success = true) {
  return new Promise((resolve, reject) => {
    console.log(`Fetching from ${url} (simulating ${success ? 'success' : 'failure'})...`);
    setTimeout(() => {
      if (success) {
        resolve(`Data from ${url}`);
      } else {
        reject(new Error(`Failed to fetch from ${url}`));
      }
    }, delay);
  });
}

// 1. Concurrent Execution
const promise1 = mockFetch('/api/users', 800, true);
const promise2 = mockFetch('/api/products', 1200, false); // Simulate failure
const promise3 = mockFetch('/api/orders', 600, true);

// 2. Aggregate All Outcomes using Promise.allSettled
console.log('--- Starting Concurrent Fetches ---');
Promise.allSettled([promise1, promise2, promise3])
  .then(results => {
    console.log('\n--- All Promises Settled ---');
    results.forEach((result, index) => {
      console.log(`Promise ${index + 1} (${result.status}):`);
      if (result.status === 'fulfilled') {
        console.log(`  Value: ${result.value}`);
      } else {
        console.error(`  Reason: ${result.reason.message}`);
      }
    });
    console.log('--- Processing complete ---');
  })
  .catch(error => {
    // This catch block will typically not be hit by Promise.allSettled
    // unless there's an error in the .then() callback itself, or if the input isn't iterable.
    console.error('Unexpected error during allSettled:', error.message);
  });

