```javascript
// Mock API functions
const fetchUsers = () => new Promise(resolve => setTimeout(() => resolve(['User1', 'User2']), 500));
const fetchProducts = () => new Promise((_, reject) => setTimeout(() => reject(new Error('Product API failed')), 200)); // Simulates failure
const fetchOrders = () => new Promise(resolve => setTimeout(() => resolve(['OrderA', 'OrderB']), 800));

async function fetchAllIndependentData() {
  try {
    // Initiate all promises concurrently without awaiting immediately
    const usersPromise = fetchUsers();
    const productsPromise = fetchProducts();
    const ordersPromise = fetchOrders();

    // Use Promise.allSettled to wait for all to complete regardless of individual success/failure
    const results = await Promise.allSettled([usersPromise, productsPromise, ordersPromise]);

    const data = {};
    const errors = [];

    results.forEach((result, index) => {
      if (result.status === 'fulfilled') {
        if (index === 0) data.users = result.value;
        else if (index === 1) data.products = result.value;
        else if (index === 2) data.orders = result.value;
      } else {
        errors.push(result.reason);
        // Optionally, assign default/null for failed categories
        if (index === 0) data.users = null; 
        else if (index === 1) data.products = null;
        else if (index === 2) data.orders = null;
      }
    });

    if (errors.length > 0) {
      // If there are any errors, we can throw an AggregateError to indicate partial failure
      // or handle them gracefully based on specific requirements.
      // For this problem, we'll throw to reflect failure.
      throw new AggregateError(errors, 'One or more API calls failed');
    }

    return data;

  } catch (error) {
    // This catch block would primarily catch errors from Promise.allSettled itself (rare)
    // or re-thrown errors like the AggregateError above.
    console.error('Critical error in fetchAllIndependentData:', error);
    throw error; // Re-throw for external handling
  }
}

// Expected usage:
fetchAllIndependentData()
  .then(data => console.log('All data fetched:', data))
  .catch(error => {
    console.error('Failed to fetch all data:', error);
    if (error instanceof AggregateError) {
      console.error('Individual errors:', error.errors.map(e => e.message));
    } else {
      console.error('Unexpected error type:', error);
    }
  });

