// Original Promise-based function
function fetchPostAndAuthorPromise(postId) {
  let postData;
  return fetch(`/api/posts/${postId}`)
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(post => {
      postData = post;
      return fetch(`/api/authors/${post.authorId}`);
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(author => {
      return { ...postData, author };
    })
    .catch(error => {
      console.error('Failed to fetch post or author:', error);
      throw error; // Re-throw to propagate
    });
}

// Refactored async/await function
async function fetchPostAndAuthorAsync(postId) {
  try {
    // Fetch post data
    const postResponse = await fetch(`/api/posts/${postId}`);
    if (!postResponse.ok) {
      throw new Error(`HTTP error! status: ${postResponse.status}`);
    }
    const postData = await postResponse.json();

    // Fetch author data using authorId from postData
    const authorResponse = await fetch(`/api/authors/${postData.authorId}`);
    if (!authorResponse.ok) {
      throw new Error(`HTTP error! status: ${authorResponse.status}`);
    }
    const author = await authorResponse.json();

    // Combine and return results
    return { ...postData, author };
  } catch (error) {
    console.error('Failed to fetch post or author:', error);
    throw error; // Re-throw to propagate
  }
}

// Example Usage (assuming mock API endpoints)
async function runExamples() {
  // Successful case
  // Mock fetch responses for success
  window.fetch = async (url) => {
    if (url.includes('/api/posts/1')) {
      return new Response(JSON.stringify({ id: 1, title: 'My Post', authorId: 101 }), { status: 200 });
    } else if (url.includes('/api/authors/101')) {
      return new Response(JSON.stringify({ id: 101, name: 'John Doe' }), { status: 200 });
    }
    return new Response('', { status: 404 });
  };

  console.log('\n--- Successful Fetch ---');
  try {
    const data = await fetchPostAndAuthorAsync(1);
    console.log('Fetched data (success):', data);
  } catch (e) {
    console.error('Error during successful fetch:', e);
  }

  // Error case (post not found)
  // Mock fetch responses for post not found
  window.fetch = async (url) => {
    if (url.includes('/api/posts/999')) {
      return new Response('Not Found', { status: 404 });
    } else if (url.includes('/api/authors/')) {
      return new Response(JSON.stringify({ id: 101, name: 'John Doe' }), { status: 200 });
    }
    return new Response('', { status: 404 });
  };

  console.log('\n--- Error Fetch (Post Not Found) ---');
  try {
    const data = await fetchPostAndAuthorAsync(999);
    console.log('Fetched data (error):', data);
  } catch (e) {
    console.error('Error during failed fetch:', e.message);
  }

  // Error case (author not found)
  // Mock fetch responses for author not found
  window.fetch = async (url) => {
    if (url.includes('/api/posts/2')) {
      return new Response(JSON.stringify({ id: 2, title: 'Another Post', authorId: 999 }), { status: 200 });
    } else if (url.includes('/api/authors/999')) {
      return new Response('Not Found', { status: 404 });
    }
    return new Response('', { status: 404 });
  };

  console.log('\n--- Error Fetch (Author Not Found) ---');
  try {
    const data = await fetchPostAndAuthorAsync(2);
    console.log('Fetched data (error):', data);
  } catch (e) {
    console.error('Error during failed fetch:', e.message);
  }
}

// Call the example runner
runExamples();
