async function fetchDataFromMultipleSources(urls) {
  // Mock fetch for testing purposes
  const mockFetch = async (url) => {
    console.log(`Attempting to fetch: ${url}`);
    return new Promise((resolve, reject) => {
      if (url.includes('success')) {
        setTimeout(() => resolve(new Response(JSON.stringify({ data: `${url}-data` }), { status: 200 })), Math.random() * 100 + 50);
      } else if (url.includes('error')) {
        setTimeout(() => resolve(new Response('Not Found', { status: 404 })), Math.random() * 100 + 50);
      } else if (url.includes('network-fail')) {
        setTimeout(() => reject(new TypeError('Failed to fetch (mock network error)')), Math.random() * 100 + 50);
      } else {
        setTimeout(() => resolve(new Response(JSON.stringify({ data: `${url}-default` }), { status: 200 })), Math.random() * 100 + 50);
      }
    });
  };

  const fetchPromises = urls.map(url => 
    mockFetch(url)
      .then(response => {
        if (!response.ok) {
          // For HTTP errors (e.g., 404, 500), explicitly throw an Error
          // so it gets caught by Promise.allSettled as a rejection.
          return Promise.reject(new Error(`HTTP Error: ${response.status} ${response.statusText || response.url}`));
        }
        return response.json();
      })
      .catch(error => {
        // Catch network errors or errors from .then() block, then re-throw
        // or return a specific rejection reason for allSettled.
        return Promise.reject(error);
      })
  );

  return await Promise.allSettled(fetchPromises);
}

