async function fetchFirstSuccessfulData(urls) {
  // Mock fetch for testing purposes
  const mockFetch = async (url) => {
    console.log(`Attempting to fetch: ${url}`);
    return new Promise((resolve, reject) => {
      if (url.includes('fast-success')) {
        setTimeout(() => resolve(new Response(JSON.stringify({ data: `${url}-data` }), { status: 200 })), 50);
      } else if (url.includes('slow-success')) {
        setTimeout(() => resolve(new Response(JSON.stringify({ data: `${url}-data` }), { status: 200 })), 500);
      } else if (url.includes('http-error')) {
        setTimeout(() => resolve(new Response('Forbidden', { status: 403 })), 100); // Resolves, but not .ok
      } else if (url.includes('network-fail')) {
        setTimeout(() => reject(new TypeError('Failed to fetch (mock network error)')), 20);
      } else {
        setTimeout(() => resolve(new Response(JSON.stringify({ data: `${url}-default` }), { status: 200 })), 150);
      }
    });
  };

  const fetchPromises = urls.map(url => 
    mockFetch(url)
      .then(response => {
        if (!response.ok) {
          // For HTTP errors, make sure it rejects so Promise.any considers it a failure
          return Promise.reject(new Error(`HTTP Error: ${response.status} ${response.statusText || response.url}`));
        }
        return response.json();
      })
      .catch(error => {
        // Catch network errors and re-throw to ensure Promise.any sees it as a rejection
        return Promise.reject(error);
      })
  );

  try {
    return await Promise.any(fetchPromises);
  } catch (error) {
    // Promise.any rejects with AggregateError if all input promises reject
    throw error; // Re-throw the AggregateError for the caller to handle
  }
}

