tasks.solutionCode:
const mockFetch = async (url) => {
  console.log(`Mock Fetching: ${url}`);
  return new Promise((resolve, reject) => {
    if (url.includes('/api/users/1')) {
      setTimeout(() => resolve(new Response(JSON.stringify({ id: 1, name: 'Alice', detailsId: 'abc' }), { status: 200 })), 100);
    } else if (url.includes('/api/user-details/abc')) {
      setTimeout(() => resolve(new Response(JSON.stringify({ age: 30, city: 'New York' }), { status: 200 })), 150);
    } else if (url.includes('/api/users/invalid')) {
      setTimeout(() => resolve(new Response('User Not Found', { status: 404 })), 50);
    } else if (url.includes('/api/user-details/invalid')) {
      setTimeout(() => resolve(new Response('Details Not Found', { status: 404 })), 50);
    } else if (url.includes('network-fail')) {
      setTimeout(() => reject(new TypeError('Mock network error')), 20);
    }
    else {
      setTimeout(() => reject(new Error('Unknown URL')), 10);
    }
  });
};

async function fetchUserProfileAndDetails(userId) {
  try {
    // 1. Fetch user profile
    const userProfileResponse = await mockFetch(`/api/users/${userId}`);
    if (!userProfileResponse.ok) {
      throw new Error(`HTTP Error fetching profile: ${userProfileResponse.status} ${userProfileResponse.statusText || userProfileResponse.url}`);
    }
    const userProfile = await userProfileResponse.json();

    // Check if detailsId exists before attempting to fetch details
    if (!userProfile.detailsId) {
      // Handle case where user might not have details (e.g., return profile only or throw specific error)
      console.warn(`User ${userId} has no detailsId.`);
      return { ...userProfile, details: null }; 
    }

    // 2. Fetch user details using detailsId
    const userDetailsResponse = await mockFetch(`/api/user-details/${userProfile.detailsId}`);
    if (!userDetailsResponse.ok) {
      throw new Error(`HTTP Error fetching details: ${userDetailsResponse.status} ${userDetailsResponse.statusText || userDetailsResponse.url}`);
    }
    const userDetails = await userDetailsResponse.json();

    // 3. Combine and return
    return { ...userProfile, details: userDetails };

  } catch (error) {
    console.error(`Failed to fetch user profile or details for userId ${userId}:`, error.message);
    throw error; // Re-throw to propagate the error to the caller
  }
}

// Test Cases:
async function runTests() {
  console.log('\n--- Test Case 1: Successful Fetch ---');
  try {
    const data = await fetchUserProfileAndDetails(1);
    console.log('Test 1 Result (Success):', data);
  } catch (e) {
    console.error('Test 1 Error:', e.message);
  }
  /* Expected:
  Test 1 Result (Success): { id: 1, name: 'Alice', detailsId: 'abc', details: { age: 30, city: 'New York' } }
  */

  console.log('\n--- Test Case 2: Profile Not Found (404) ---');
  try {
    const data = await fetchUserProfileAndDetails('invalid');
    console.log('Test 2 Result (Should not be here):', data);
  } catch (e) {
    console.error('Test 2 Error (Profile Not Found):', e.message);
  }
  /* Expected: Test 2 Error (Profile Not Found): HTTP Error fetching profile: 404 User Not Found */

  console.log('\n--- Test Case 3: Details Not Found (404) ---');
  // To simulate this, we need a profile that returns a valid detailsId but the details endpoint fails
  // Temporarily adjust mock fetch for this specific test
  const originalMockFetch = global.mockFetch;
  global.mockFetch = async (url) => {
    if (url.includes('/api/users/2')) {
      return new Response(JSON.stringify({ id: 2, name: 'Bob', detailsId: 'invalid-details' }), { status: 200 });
    } else if (url.includes('/api/user-details/invalid-details')) {
      return new Response('Details Not Found', { status: 404 });
    } else {
      return originalMockFetch(url); // Fallback to original mock for other calls
    }
  };
  try {
    const data = await fetchUserProfileAndDetails(2);
    console.log('Test 3 Result (Should not be here):', data);
  } catch (e) {
    console.error('Test 3 Error (Details Not Found):', e.message);
  } finally {
    global.mockFetch = originalMockFetch; // Restore original mock
  }
  /* Expected: Test 3 Error (Details Not Found): HTTP Error fetching details: 404 Details Not Found */

  console.log('\n--- Test Case 4: Network Error during Profile Fetch ---');
  try {
    const data = await fetchUserProfileAndDetails('network-fail');
    console.log('Test 4 Result (Should not be here):', data);
  } catch (e) {
    console.error('Test 4 Error (Network Failure):', e.message);
  }
  /* Expected: Test 4 Error (Network Failure): Mock network error */

  console.log('\n--- Test Case 5: User with no detailsId ---');
  // Mock fetch responses for user with no detailsId
  const originalMockFetch2 = global.mockFetch;
  global.mockFetch = async (url) => {
    if (url.includes('/api/users/no-details')) {
      return new Response(JSON.stringify({ id: 'no-details', name: 'Charlie', detailsId: null }), { status: 200 });
    }
    return originalMockFetch2(url); 
  };
  try {
    const data = await fetchUserProfileAndDetails('no-details');
    console.log('Test 5 Result (No Details):', data);
  } catch (e) {
    console.error('Test 5 Error:', e.message);
  } finally {
    global.mockFetch = originalMockFetch2;
  }
  /* Expected: Test 5 Result (No Details): { id: 'no-details', name: 'Charlie', detailsId: null, details: null } */
}

// Make mockFetch globally available for testing context
global.mockFetch = mockFetch;

runTests();

