    setTimeout(() => {
      if (id === 0) {
        reject(new Error('Network Error: User ID 0 is reserved.'));
      } else {
        resolve({ id: id, name: id === 99 ? '' : `User ${id}`, email: `user${id}@example.com` });
      }
    }, 500);
  });
