async function validateUser(user) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      if (!user || !user.name) {
        reject(new Error('Validation Error: User name cannot be empty.'));
      } else {
        resolve(user);
      }
    }, 200);
  });
}

async function saveUser(user) {
  return new Promise(resolve => {
    setTimeout(() => {
      console.log(`User ${user.id} saved successfully!`);
      resolve(true);
    }, 300);
  });
}

async function processUserWorkflow(userId) {
  let user;
  try {
    console.log(`Starting workflow for user ${userId}...`);
    user = await fetchUser(userId);
    console.log(`User ${userId} fetched.`);

    await validateUser(user);
    console.log(`User ${userId} validated.`);

    await saveUser(user);
    console.log(`Workflow for user ${userId} completed successfully.`);
    return true;
  } catch (error) {
    if (error.message.includes('Network Error')) {
      console.error(`Workflow Failed: ${error.message}`);
    } else if (error.message.includes('Validation Error')) {
      console.error(`Workflow Failed: ${error.message}`);
    } else {
      console.error(`An unexpected error occurred during workflow for user ${userId}: ${error.message}`);
    }
    return false;
  }
}

// Test cases
