```typescript
interface FetchOptions extends RequestInit {
  timeout?: number;
  retries?: number;
  retryDelay?: number;
}

async function fetchWithRetryAndTimeout(url: string, options: FetchOptions = {}): Promise<any> {
  const { timeout = 5000, retries = 3, retryDelay = 500, ...fetchInit } = options;

  for (let i = 0; i <= retries; i++) {
    const controller = new AbortController();
    const { signal } = controller;
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
      const response = await fetch(url, { signal, ...fetchInit });
      clearTimeout(timeoutId);

      if (!response.ok) {
        // HTTP errors (4xx, 5xx) are not retried by default, throw immediately
        const errorBody = await response.text();
        throw new Error(`HTTP error! Status: ${response.status}. Body: ${errorBody.substring(0, 100)}`);
      }

      return await response.json();
    } catch (error: any) {
      clearTimeout(timeoutId); // Clear timeout even if fetch fails for other reasons

      const isNetworkError = error.name === 'TypeError' || error.name === 'AbortError';

      if (isNetworkError && i < retries) {
        console.warn(`Attempt ${i + 1}/${retries + 1} failed for ${url}: ${error.message}. Retrying in ${retryDelay}ms...`);
        await new Promise(resolve => setTimeout(resolve, retryDelay));
        continue; // Go to next retry attempt
      } else {
        // If not a network error, or max retries reached, re-throw
        console.error(`Final attempt failed for ${url}: ${error.message}`);
        throw error;
      }
    }
  }
}

// Example Usage (Test Cases)
(async () => {
  console.log('--- Test Case 1: Successful Fetch ---');
  try {
    const data = await fetchWithRetryAndTimeout('https://jsonplaceholder.typicode.com/posts/1', { timeout: 10000 });
    console.log('Success:', data.title.substring(0, 30) + '...');
  } catch (err: any) {
    console.error('Failure:', err.message);
  }

  console.log('\n--- Test Case 2: Simulating Timeout and Retry ---');
  // To reliably test timeout, you'd need a server that can introduce delays.
  // `jsonplaceholder.typicode.com` often has _delay param, but it's not always reliable in all environments.
  // This example simulates a timeout by making the API call take longer than the local timeout.
  try {
    const data = await fetchWithRetryAndTimeout('https://jsonplaceholder.typicode.com/posts/1?_delay=6000', { timeout: 2000, retries: 2 });
    console.log('Success (after retry/timeout test):', data.title.substring(0, 30) + '...');
  } catch (err: any) {
    console.error('Failure (after retry/timeout test):', err.message);
  }

  console.log('\n--- Test Case 3: Simulating Network Error and Retry ---');
  // Temporarily change to a non-existent domain to simulate DNS/network error for retries
  try {
    const data = await fetchWithRetryAndTimeout('http://nonexistent-domain-for-test.com/data', { retries: 3 });
    console.log('Success (should not happen):', data);
  } catch (err: any) {
    console.error('Failure (Network error, retried):', err.message);
  }

  console.log('\n--- Test Case 4: Simulating HTTP Error (No Retry) ---');
  try {
    const data = await fetchWithRetryAndTimeout('https://jsonplaceholder.typicode.com/non-existent-path-404', { retries: 3 });
    console.log('Success (should not happen):', data);
  } catch (err: any) {
    console.error('Failure (HTTP 404 error, no retry):', err.message);
  }
})();
```

