async function fetchWithTimeout(url, timeout = 5000) {
  const controller = new AbortController();
  // Set up a timeout to abort the request
  const id = setTimeout(() => controller.abort(), timeout);

  try {
    // Initiate fetch with the signal
    const response = await fetch(url, { signal: controller.signal });
    // Clear the timeout if fetch completes successfully
    clearTimeout(id);

    // Check for HTTP errors (e.g., 404, 500)
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    return await response.json();
  } catch (error) {
    // Always clear timeout in catch block too to prevent hanging timers
    clearTimeout(id);

    // Distinguish timeout errors from other errors
    if (error.name === 'AbortError') {
      throw new Error(`Request timed out after ${timeout}ms`);
    }
    // Re-throw other errors
    throw error;
  }
}

// Example usage:
// (async () => {
//   try {
//     const data = await fetchWithTimeout('https://jsonplaceholder.typicode.com/todos/1', 2000);
//     console.log('Fetched data:', data);
//   } catch (e) {
//     console.error('Fetch error:', e.message);
//   }
// })();

async function fetchWithRetry(url, options = {}, retries = 3, delay = 1000) {
  try {
    // Attempt the fetch operation
    const response = await fetch(url, options);

    // If response is not OK, consider it a failure for retry purposes
    // (This example retries on any fetch error, but in practice, you'd check response.ok or specific status codes)
    if (!response.ok && response.status >= 500) { // Example: Only retry 5xx errors
       throw new Error(`Server error: ${response.status}`);
    }

    return response;
  } catch (error) {
    // Check if retries are exhausted
    if (retries <= 1) {
      console.error(`Fetch failed after ${retries} attempt(s).`);
      throw error; // Re-throw the original error
    }

    console.warn(`Fetch failed, retrying in ${delay}ms... (Attempts left: ${retries - 1})`);

    // Wait before retrying using a Promise-based delay
    await new Promise(resolve => setTimeout(resolve, delay));

    // Recursively call itself with decremented retries and increased delay
    return fetchWithRetry(url, options, retries - 1, delay * 2); // Exponential backoff
  }
}

// Example usage:
