class CancellableFetcher {
  private controller: AbortController | null = null;
  private timeoutId: NodeJS.Timeout | null = null;

  constructor() {}

  async fetchData(url: string, timeoutMs: number, onProgress?: (msg: string) => void): Promise<any> {
    if (this.controller) {
      // Abort any existing ongoing request before starting a new one
      this.cancel();
      if (onProgress) onProgress('Previous request aborted for new one.');
    }

    this.controller = new AbortController();
    const signal = this.controller.signal;
    let isTimeout = false;

    if (onProgress) onProgress('Fetching...');

    this.timeoutId = setTimeout(() => {
      isTimeout = true; // Flag to distinguish timeout from manual cancel
      this.controller?.abort();
    }, timeoutMs);

    try {
      const response = await fetch(url, { signal });

      if (this.timeoutId) {
        clearTimeout(this.timeoutId);
        this.timeoutId = null;
      }
      this.controller = null; // Clear controller after successful completion

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      if (onProgress) onProgress('Fetch successful!');
      return data;
    } catch (error: any) {
      if (this.timeoutId) {
        clearTimeout(this.timeoutId);
        this.timeoutId = null;
      }
      this.controller = null; // Clear controller after error

      if (error.name === 'AbortError') {
        if (onProgress) {
          onProgress(isTimeout ? `Timed out after ${timeoutMs}ms!` : 'Request cancelled!');
        }
        // Re-throw specific error messages for external handling
        throw new Error(isTimeout ? `Request timed out after ${timeoutMs}ms` : 'Request cancelled by user');
      } else {
        if (onProgress) onProgress(`Error: ${error.message}`);
        throw error;
      }
    }
  }

  cancel(): void {
    if (this.controller) {
      this.controller.abort();
      this.controller = null;
    }
    if (this.timeoutId) {
      clearTimeout(this.timeoutId);
      this.timeoutId = null;
    }
  }
}

