interface RetryOptions {
  attempts?: number;
  delay?: number;
  backoffFactor?: number;
  shouldRetry?: (error: any) => boolean;
}

async function retry<T>(fn: () => Promise<T>, options?: RetryOptions): Promise<T> {
  const { 
    attempts = 3,
    delay = 1000,
    backoffFactor = 2,
    shouldRetry = () => true // Default: always retry
  } = options || {};

  let currentDelay = delay;
  for (let i = 0; i < attempts; i++) {
    try {
      return await fn();
    } catch (error: any) {
      // Check if this error should trigger a retry and if attempts are left
      if (i < attempts - 1 && shouldRetry(error)) {
        console.warn(`Attempt ${i + 1} failed. Retrying in ${currentDelay}ms... (Error: ${error.message})`);
        await new Promise(resolve => setTimeout(resolve, currentDelay));
        currentDelay *= backoffFactor; // Increase delay for next attempt
      } else {
        // No more attempts or error is not retryable, re-throw
        console.error(`Operation failed after ${i + 1} attempt(s). Last error: ${error.message}`);
        throw error; 
      }
    }
  }
  // This part should ideally not be reached if initial 'attempts' is > 0 and loop is correctly structured.
  // Added for strict typing, though the loop ensures an eventual return or throw.
  throw new Error('Retry logic exhausted without success.');
}

