async function fetchWithRetry(url, options = {}, retries = 3, backoff = 300) {
  try {
    console.log(`Attempting fetch for ${url} (retries left: ${retries})`);
    const response = await fetch(url, options);
    if (!response.ok) {
      // Consider non-2xx responses as errors for retry purposes, e.g., 5xx errors
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    return response;
  } catch (error) {
    console.error(`Fetch failed: ${error.message}`);
    if (retries > 0) {
      const delay = backoff * Math.pow(2, 3 - retries);
      console.log(`Retrying in ${delay}ms...`);
      await new Promise(resolve => setTimeout(resolve, delay));
      return fetchWithRetry(url, options, retries - 1, backoff);
    }
    throw error; // Re-throw if no retries left
  }
}

