async function concurrentLimit(tasks, limit) {
  const results = [];
  const executing = [];

  for (const task of tasks) {
    // Wrap task in a Promise to ensure it's a promise, and to track its resolution.
    // We immediately push this promise to results, as we want to collect ALL results.
    const p = Promise.resolve().then(() => task());
    results.push(p);

    if (limit <= tasks.length) {
      // 'e' represents the promise that resolves when 'p' finishes AND removes itself
      // from the 'executing' queue. This is crucial for freeing up a slot.
      const e = p.then(() => executing.splice(executing.indexOf(e), 1));
      executing.push(e);

      // If we've hit the limit, wait for at least one task to complete.
      // Promise.race allows us to wait for *any* of the currently executing
      // tasks to finish, freeing up a slot for the next task in the loop.
      if (executing.length >= limit) {
        await Promise.race(executing);
      }
    }
  }

  // Wait for all tasks (both currently executing and those that were queued) to complete.
  return Promise.all(results);
}

