async function concurrentLimit(tasks, limit) {
  const results = []; // Stores promises for all task outcomes, in original order
  const executing = []; // Stores promises for tasks currently running

  for (let i = 0; i < tasks.length; i++) {
    const task = tasks[i];

    // Create a promise for the current task. Use Promise.resolve() to ensure consistency
    // and immediately call the task function.
    const taskPromise = Promise.resolve().then(() => task());
    results.push(taskPromise); // Store its promise in results to maintain order

    // Create a promise 'completionPromise' that resolves when taskPromise completes.
    // This is the one we add to 'executing' and remove when it's done.
    // Using .finally() ensures it's removed whether it resolves or rejects.
    const completionPromise = taskPromise.finally(() => {
      // Remove the completionPromise from the executing array
      executing.splice(executing.indexOf(completionPromise), 1);
    });
    executing.push(completionPromise);

    // If the limit is reached, wait for any currently executing task to finish
    if (executing.length >= limit) {
      await Promise.race(executing);
    }
  }

  // Wait for all tasks to complete, regardless of success or failure.
  // Promise.all handles rejection correctly if any input promise rejects.
  return Promise.all(results);
}

