function createCancellablePromise(promise, cleanup = (isCancelled) => {}) {
  let isCancelled = false;

  const wrappedPromise = new Promise((resolve, reject) => {
    promise.then(
      value => {
        if (isCancelled) {
          cleanup(true); // Execute cleanup on cancellation
          reject({ isCancelled: true, message: 'Promise cancelled' });
        } else {
          resolve(value);
        }
      },
      error => {
        if (isCancelled) {
          cleanup(true); // Execute cleanup on cancellation, even if original rejected
          reject({ isCancelled: true, message: 'Promise cancelled due to error' });
        } else {
          cleanup(false); // Execute cleanup on non-cancellation error
          reject(error);
        }
      }
    );
  });

  return {
    promise: wrappedPromise,
    cancel: () => { 
      isCancelled = true; 
      // If the promise hasn't settled yet, the cleanup will be called 
      // when its original .then/.catch fires and sees isCancelled = true.
      // If you needed immediate cleanup regardless of original promise state,
      // you might add cleanup() here, but be careful with async cleanup.
    }
  };
}

