async function fetchWithRetry(url, options = {}, retries = 3, initialBackoff = 300) {
  let currentRetries = retries;
  const originalRetries = retries;

  while (currentRetries >= 0) {
    try {
      console.log(`Attempting fetch for ${url} (retries left: ${currentRetries}, total attempts: ${originalRetries - currentRetries + 1})`);
      const response = await fetch(url, options);
      if (!response.ok) {
        // Treat non-2xx responses as errors that trigger retries
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response;
    } catch (error) {
      console.error(`Fetch failed: ${error.message}`);
      if (currentRetries > 0) {
        const retriesAttempted = originalRetries - currentRetries; // How many retries have already happened
        const baseDelay = initialBackoff * Math.pow(2, retriesAttempted);
        const delayWithJitter = Math.random() * baseDelay; // Full jitter

        console.log(`Retrying in ${delayWithJitter.toFixed(0)}ms (max ${baseDelay}ms) ...`);
        await new Promise(resolve => setTimeout(resolve, delayWithJitter));
        currentRetries--;
      } else {
        throw error; // Re-throw the error if no retries left
      }
    }
  }
}

