async function concurrencyPool(tasks, limit) {
  const results = [];
  const executing = new Set(); // Using a Set for efficient add/delete of executing promises

  for (const taskFn of tasks) {
    // Wrap the task function call in a Promise.resolve() to ensure consistency
    // and capture the promise returned by the task.
    const taskPromise = Promise.resolve().then(() => taskFn());
    results.push(taskPromise); // Store this promise to collect all results in order

    // Create a 'completion promise' that removes itself from the executing set
    // when it settles (either resolves or rejects). Using .finally() ensures this.
    const completionPromise = taskPromise.finally(() => {
      executing.delete(completionPromise);
    });
    executing.add(completionPromise); // Add to the set of currently executing promises

    // If the number of executing tasks reaches the limit, wait for one to finish.
    // Promise.race waits for the *first* promise in the set to settle.
    if (executing.size >= limit) {
      await Promise.race(executing); 
    }
  }

  // Wait for all tasks to genuinely complete (including those that were queued).
  // Promise.all will resolve with an array of results or reject if any promise rejected.
  return Promise.all(results);
}

