let activeController = null; // Stores the AbortController for the currently active fetch
let activePromise = null;    // Stores the promise of the currently active fetch operation

async function debounceFetch(url, options = {}) {
  // 1. If there's an active request, abort it.
  if (activeController) {
    activeController.abort();
  }

  // 2. Create a new AbortController for the current request.
  const currentController = new AbortController();
  const signal = currentController.signal;
  activeController = currentController; // Set this as the new active controller

  // 3. Create a promise for the current fetch operation.
  // We need to wrap it so we can specifically handle AbortError.
  const currentFetchPromise = new Promise(async (resolve, reject) => {
    try {
      const response = await fetch(url, { ...options, signal });
      // Important: After fetch resolves, check if *this* specific fetch was cancelled by a later call.
      // This handles the race condition where the fetch succeeds, but then a new debounceFetch call
      // comes in and aborts *this* one before its then/catch handlers execute.
      if (activeController !== currentController) {
        // This means a newer request has taken over. Treat this one as effectively cancelled.
        // We don't want to resolve/reject for an outdated request.
        return reject({ name: 'StaleRequestError', message: 'This request was superseded.' });
      }
      resolve(response);
    } catch (error) {
      if (error.name === 'AbortError') {
        // This is the intended cancellation. We should not propagate this as a regular error.
        // Only reject if it's not the currently active request that got aborted.
        if (activeController === currentController) {
          // This indicates this request was aborted by an external mechanism, and it's currently active
          // This specific pattern here aims to handle cases where *this* exact request was aborted.
          // For debounce, we want to reject silently if *this* specific call was aborted by a new call.
          reject(error); // This will then be caught by the outer catch to differentiate
        } else {
           // If activeController is already different, it means *this* promise was aborted by a newer call.
           // In debounce, we usually want to silently ignore these.
           // The caller of the `activePromise` (which is `currentFetchPromise`) will then reject if awaited.
           reject(error);
        }
      } else if (activeController !== currentController) {
          // If a new request has taken over, and this one errors out, silently ignore it as well.
          reject({ name: 'StaleRequestError', message: 'This request failed but was superseded.' });
      } else {
        // Regular error not related to cancellation
        reject(error);
      }
    }
  });

  activePromise = currentFetchPromise; // Store the promise for external waiting

  // Return a promise that only resolves if it's still the active one,
  // and also handles the AbortError internally without propagating it to the outside `then` chain.
  return new Promise((resolve, reject) => {
    currentFetchPromise.then(response => {
      if (activeController === currentController) {
        resolve(response);
      } else {
        // This should theoretically be handled by the currentFetchPromise's internal logic
        // but this adds an extra layer of safety for race conditions.
        reject(new Error('Previous request superseded, results ignored.'));
      }
    }).catch(error => {
      if (error.name === 'AbortError' || error.name === 'StaleRequestError') {
        // Silently catch the abortion or stale request error for debounce pattern.
        // This makes the returned promise neither resolve nor reject for cancelled requests.
        // console.log(`Request for ${url} was cancelled/superseded.`);
        // Do nothing, the promise effectively 'hangs' or is never fulfilled from this perspective.
      } else {
        reject(error); // Propagate actual errors
      }
    });
  });
}

