const PI = Math.PI;

// Define common methods outside to avoid recreation per instance
const commonShapeMethods = {
  getType() {
    return this.type; // 'this' will refer to the specific shape object
  },
  // getArea will be defined per shape type or conditionally
};

function createShape(type, options) {
  let shape;

  switch (type.toLowerCase()) {
    case 'circle':
      if (typeof options.radius !== 'number' || options.radius < 0) {
        throw new Error('Circle requires a positive numeric radius.');
      }
      shape = {
        type: 'Circle',
        radius: options.radius,
        getArea: () => PI * options.radius * options.radius // Uses closure over options.radius
      };
      break;
    case 'rectangle':
      if (typeof options.width !== 'number' || typeof options.height !== 'number' || options.width < 0 || options.height < 0) {
        throw new Error('Rectangle requires positive numeric width and height.');
      }
      shape = {
        type: 'Rectangle',
        width: options.width,
        height: options.height,
        getArea: () => options.width * options.height // Uses closure over options.width/height
      };
      break;
    default:
      throw new Error(`Unknown shape type: ${type}`);
  }

  // Merge common methods. Here, `getType` uses `this` which refers to the `shape` object.
  // `getArea` is defined as a closure for specific properties, which is acceptable for factories
  // for encapsulation, though it means a new function per instance.
  return Object.assign(shape, commonShapeMethods);
}

