tasks.solutionCode:
function createUserManager() {
  // Private state using closure
  let users = []; 

  return {
    /**
     * Adds a user to the manager.
     * @param {object} user - The user object ({ id, name, email }).
     * @throws {Error} If a user with the same ID already exists.
     */
    addUser(user) {
      if (!user || !user.id) {
        throw new Error('User object must have an id.');
      }
      if (users.some(u => u.id === user.id)) {
        throw new Error(`User with ID '${user.id}' already exists.`);
      }
      users.push({ ...user }); // Store a copy to prevent external modification
    },

    /**
     * Retrieves a user by ID.
     * @param {string} id - The ID of the user to retrieve.
     * @returns {object|null} The user object or null if not found.
     */
    getUser(id) {
      const user = users.find(u => u.id === id);
      return user ? { ...user } : null; // Return a copy
    },

    /**
     * Updates an existing user by ID.
     * @param {string} id - The ID of the user to update.
     * @param {object} updates - An object containing partial updates.
     * @throws {Error} If the user is not found.
     */
    updateUser(id, updates) {
      const userIndex = users.findIndex(u => u.id === id);
      if (userIndex === -1) {
        throw new Error(`User with ID '${id}' not found.`);
      }
      // Prevent updating the ID
      if (updates && updates.id && updates.id !== id) {
        throw new Error('Cannot change user ID.');
      }
      users[userIndex] = { ...users[userIndex], ...updates };
    },

    /**
     * Deletes a user by ID.
     * @param {string} id - The ID of the user to delete.
     * @returns {boolean} True if deleted, false if not found.
     */
    deleteUser(id) {
      const initialLength = users.length;
      users = users.filter(u => u.id !== id);
      return users.length < initialLength;
    },

    /**
     * Returns a copy of all users.
     * @returns {Array<object>} A new array containing copies of all user objects.
     */
    getAllUsers() {
      return users.map(user => ({ ...user })); // Return deep copy if users can be nested objects
    }
  };
}

