  configurable: false  // Prevents id from being deleted or its attributes changed
});

console.log(user.name); // John Doe
user.name = 'Jane Doe';
console.log(user.name); // Jane Doe

console.log(user.id);   // 12345
try {
  user.id = 54321; // Attempt to change read-only property
} catch (e) {
