  configurable: true // Allow it to be deleted or reconfigured
});

console.log(person.fullName); // Getting fullName...
                              // John Doe

person.fullName = 'Jane Smith'; // Setting fullName to: Jane Smith
console.log(person.firstName); // Jane
console.log(person.lastName);  // Smith
