tasks.solutionCode:
function curry(fn) {
  const expectedArgs = fn.length;

  return function curried(...args) {
    // Check if enough arguments have been collected
    if (args.length >= expectedArgs) {
      // If yes, execute the original function with collected arguments
      return fn.apply(this, args);
    } else {
      // If not enough arguments, return a new function that will collect more
      const self = this;
      return function(...nextArgs) {
        // Concatenate new arguments with previously collected ones
        // Recursively call the curried function with all accumulated arguments
        return curried.apply(self, args.concat(nextArgs));
      };
    }
  };
}

