function debounce(fn, delay) {
  let timerId = null;

  return function(...args) {
    const context = this;

    // Clear any existing timer to reset the countdown
    if (timerId) {
      clearTimeout(timerId);
    }

    // Set a new timer
    timerId = setTimeout(() => {
      // Execute the original function with the correct 'this' context and arguments
      fn.apply(context, args);
      timerId = null; // Optional: clear timerId after execution for cleaner state
    }, delay);
  };
