const SessionManager = (function() {
  let _isLoggedIn = false; // Private
  let _username = null;    // Private
  let _token = null;       // Strictly private, never exposed

  function _resetSession() { // Private helper for internal use
    _isLoggedIn = false;
    _username = null;
    _token = null;
  }

  return {
    login: function(username, token) {
      if (!username || !token) {
        console.error('Login failed: username and token are required.');
        return;
      }
      _isLoggedIn = true;
      _username = username;
      _token = token;
      console.log(`User '${_username}' logged in.`);
    },
    logout: function() {
      _resetSession();
      console.log('User logged out.');
    },
    getStatus: function() {
      return {
        isLoggedIn: _isLoggedIn,
        username: _username
      };
    },
    // A method to simulate checking if a token is valid (could be used internally by other features)
    _checkTokenValidity: function(inputToken) {
        return inputToken === _token; // Private method for internal module use (though exposed here for testing)
    }
  };
})();

