function map(arr, transformFn) {
  const result = [];
  for (let i = 0; i < arr.length; i++) {
    result.push(transformFn(arr[i]));
  }
  return result; // Returns a new array, does not mutate original
}

function filter(arr, predicateFn) {
  const result = [];
  for (let i = 0; i < arr.length; i++) {
    if (predicateFn(arr[i])) {
      result.push(arr[i]);
    }
  }
  return result; // Returns a new array, does not mutate original
}

function createMultiplier(factor) {
  return function(num) {
    return num * factor; // Pure: no side effects, deterministic
  };
}

function isEven(num) {
  return num % 2 === 0; // Pure: no side effects, deterministic
}

