/**
 * A pure function that adds two numbers.
 * - Always returns the same result for the same inputs (e.g., add(2, 3) always returns 5).
 * - Has no side effects (doesn't modify external state or perform I/O).
 */
function add(a, b) {
  return a + b;
}

let c = 10;
/**
 * An impure function that adds a number to an external variable 'c'.
 * - Its output depends on the external state of 'c', which can change.
 * - Modifies an external variable 'c' if it were to increment 'c' inside.
 */
function addToC(x) {
  return x + c; // Depends on external variable 'c'
}

// Demonstrating impurity:
console.log(addToC(5)); // If c is 10, returns 15
c = 20;
console.log(addToC(5)); // If c is 20, returns 25 (different output for same input '5')

/**
 * A higher-order function that uses a closure to create a pure adder.
 * The returned inner function is pure because it only depends on its immediate inputs
 * and the 'a' value captured from its lexical environment, which remains constant.
 */
function createPureAdder(a) {
  // 'a' is captured in the closure. It acts as a fixed part of the inner function's context.
  return function(b) {
    return a + b; // 'a' is from the closure, 'b' is the explicit argument
  };
}

const add5 = createPureAdder(5);
console.log(add5(10)); // Always returns 15 given input 10 (5 + 10)
console.log(add5(20)); // Always returns 25 given input 20 (5 + 20)

const add10 = createPureAdder(10);
console.log(add10(3)); // Always returns 13 given input 3 (10 + 3)

// Problem with var in loops
function createFunctions() {
  var funcs = [];

  for (var i = 0; i < 3; i++) {
    // Each function closes over the *same* 'i' variable.
    // By the time these functions are called, 'i' is already 3.
    funcs.push(function() {
      console.log(i);
    });
  }

  return funcs;
}

const functions = createFunctions();
functions[0](); // Expected 0, but outputs 3
functions[1](); // Expected 1, but outputs 3
functions[2](); // Expected 2, but outputs 3

// Solution using let (block scope)
function createFunctionsFixed() {
  const funcs = [];

  for (let i = 0; i < 3; i++) {
    // For each iteration, a *new* 'i' binding is created due to 'let's block scope.
    // Each function closes over its own 'i' for that specific iteration.
    funcs.push(function() {
      console.log(i);
    });
  }

  return funcs;
}

const fixedFunctions = createFunctionsFixed();
fixedFunctions[0](); // Outputs 0
fixedFunctions[1](); // Outputs 1
fixedFunctions[2](); // Outputs 2

const Counter = (function() {
  // privateCounter is a 'private' variable, accessible only within the IIFE's scope
  let privateCounter = 0;

  // changeBy is a 'private' function, also only accessible within this scope
  function changeBy(val) {
    privateCounter += val;
  }

  // The IIFE returns an object, which becomes the public interface of the Counter module
  return {
    increment: function() {
      // This public method accesses and modifies privateCounter via the changeBy private function
      changeBy(1);
    },
    decrement: function() {
      changeBy(-1);
    },
    value: function() {
      // This public method provides read-only access to privateCounter
