/**
 * Transforms an array of user data based on a configuration.
 * This function must be pure.
 * 
 * @param {Array<Object>} users - An array of raw user objects.
 * @param {Object} config - Configuration object.
 * @param {boolean} [config.anonymizeEmail=false] - Whether to anonymize user emails.
 * @param {Array<string>} [config.fieldsToInclude=['id', 'firstName', 'lastName', 'email', 'isActive']] - Array of fields to include in the output objects.
 * @returns {Array<Object>} A new array of transformed user objects.
 */
function transformUserData(users, config = {}) {
  // Ensure config has default values and immutability
  const effectiveConfig = {
    anonymizeEmail: typeof config.anonymizeEmail === 'boolean' ? config.anonymizeEmail : false,
    fieldsToInclude: Array.isArray(config.fieldsToInclude) ? [...config.fieldsToInclude] : ['id', 'firstName', 'lastName', 'email', 'isActive'],
    // Deep copy other properties if needed, but for primitive config, spread is fine.
    ...config
  };

  if (!Array.isArray(users)) {
    return []; // Handle invalid input gracefully for purity
  }

  const processedUsers = users
    .filter(user => user && user.isActive === true) // 1. Filter by Active Status
    .map(user => {
      // Create a new user object to avoid mutating the original
      const newUser = { ...user };

      // 2. Add fullName
      if (newUser.firstName && newUser.lastName) {
        newUser.fullName = `${newUser.firstName} ${newUser.lastName}`;
      }

      // 3. Anonymize Email (conditionally)
      if (effectiveConfig.anonymizeEmail && newUser.email) {
        newUser.email = 'anonymized@example.com';
      }

      return newUser;
    })
    .map(user => {
      // 4. Select Fields
      const selectedFieldsUser = {};
      effectiveConfig.fieldsToInclude.forEach(field => {
        if (user.hasOwnProperty(field)) {
          selectedFieldsUser[field] = user[field];
        }
      });
      return selectedFieldsUser;
