/**
 * Creates an array of click handler functions, each logging a specific item.
 * @param {Array<string>} items - An array of item names.
 * @returns {Array<Function>} An array of functions, where each function logs a specific item.
 */
function createClickHandlers(items) {
  const handlers = [];

  for (let i = 0; i < items.length; i++) {
    // Using 'let' ensures that a new 'i' (and thus a new 'item' lookup) is bound for each iteration,
    // and the closure captures the 'item' value specific to that iteration.
    const item = items[i]; // Explicitly capture item for clarity, though not strictly necessary with 'let i'
    handlers.push(function() {
      console.log(`Clicked on: ${item}`);
    });
  }

  return handlers;
}

