/**
 * ThemeManager module for managing application themes.
 * Implemented using the Module Pattern.
 */
const ThemeManager = (function() {
  // Private variables
  let currentTheme = 'light'; // Default theme
  const validThemes = ['light', 'dark', 'contrast'];

  // Private helper function to find the next theme in the cycle
  function getNextThemeInCycle() {
    const currentIndex = validThemes.indexOf(currentTheme);
    const nextIndex = (currentIndex + 1) % validThemes.length;
    return validThemes[nextIndex];
  }

  return { // Public interface
    getTheme: function() {
      return currentTheme;
    },
    setTheme: function(newTheme) {
      if (validThemes.includes(newTheme)) {
        currentTheme = newTheme;
        console.log(`Theme set to: ${currentTheme}`);
        return true;
      } else {
        console.warn(`Invalid theme: '${newTheme}'. Valid themes are: ${validThemes.join(', ')}`);
        return false;
      }
    },
    getValidThemes: function() {
      // Return a copy to prevent external mutation
      return [...validThemes]; 
    },
    toggleTheme: function() {
      currentTheme = getNextThemeInCycle();
      console.log(`Theme toggled to: ${currentTheme}`);
      return currentTheme;
    }
  };
})();

