function isDeepEqual(obj1, obj2) {
  if (obj1 === obj2) return true; // Strict equality check for primitives and same object reference

  if (typeof obj1 !== 'object' || obj1 === null || typeof obj2 !== 'object' || obj2 === null) {
    return false; // If not objects or one is null, and not strictly equal, they are not deep equal
  }

  // Handle Arrays
  if (Array.isArray(obj1) && Array.isArray(obj2)) {
    if (obj1.length !== obj2.length) return false;
    for (let i = 0; i < obj1.length; i++) {
      if (!isDeepEqual(obj1[i], obj2[i])) return false;
    }
    return true;
  }

  // If one is array and other is not, they are not deep equal
  if (Array.isArray(obj1) !== Array.isArray(obj2)) return false;

  // Handle Objects
  const keys1 = Object.keys(obj1);
  const keys2 = Object.keys(obj2);

  if (keys1.length !== keys2.length) return false; // Different number of properties

  for (const key of keys1) {
    if (!keys2.includes(key) || !isDeepEqual(obj1[key], obj2[key])) {
      return false; // Key missing or values not deep equal
    }
  }

  return true;
}

// Test cases
const objA = { a: 1, b: { c: 2, d: [3, 4] }, e: 'test' };
const objB = { a: 1, b: { c: 2, d: [3, 4] }, e: 'test' };
const objC = { a: 1, b: { c: 2, d: [3, 5] }, e: 'test' };
const objD = { a: 1, b: { c: 2 }, e: 'test' };

console.log(isDeepEqual(objA, objB)); // true
console.log(isDeepEqual(objA, objC)); // false
console.log(isDeepEqual(objA, objD)); // false
console.log(isDeepEqual(null, null)); // true
console.log(isDeepEqual(1, 1));       // true
console.log(isDeepEqual(1, '1'));     // false
console.log(isDeepEqual({}, {}));     // true
console.log(isDeepEqual([], []));     // true
console.log(isDeepEqual([1, {a:1}], [1, {a:1}])); // true
console.log(isDeepEqual([1, {a:1}], [1, {a:2}])); // false
```

