tasks.solutionCode:
function transformObject(inputObject, transformationMap) {
  if (!inputObject || typeof inputObject !== 'object' || Array.isArray(inputObject)) {
    // For simplicity, we'll only process plain objects. Arrays and primitives are returned as is.
    // A full solution would recursively deep copy arrays as well.
    return inputObject;
  }

  // Step 1: Create a deep copy of the inputObject.
  // We use JSON.parse(JSON.stringify) for simplicity, aware of its limitations.
  const clonedObject = JSON.parse(JSON.stringify(inputObject));

  const result = {};

  // Step 2: Iterate over the cloned object's own properties
  for (const key in clonedObject) {
    if (Object.prototype.hasOwnProperty.call(clonedObject, key)) {
      const originalValue = clonedObject[key];
      const transformRule = transformationMap[key];

      if (transformRule) {
        if (typeof transformRule === 'string') {
          // Rename property
          result[transformRule] = originalValue;
        } else if (typeof transformRule === 'function') {
          // Transform value
          const newValue = transformRule(originalValue);
          result[key] = newValue;
        } else {
          // If transformationMap[key] is neither string nor function, copy as is
          result[key] = originalValue;
        }
      } else {
        // Copy property as is if no transformation rule exists
        result[key] = originalValue;
      }
    }
  }

  return result;
}

