import React from 'react';

function GreetingList() {
  return (
    <>
      <h1>Hello!</h1>
      <p>Welcome to the example.</p>
    </>
  );
}

// This component, when rendered, will output directly:
// <h1>Hello!</h1>
// <p>Welcome to the example.</p>
// without an enclosing div.

import React from 'react';

interface ItemProps {
  id: number;
  name: string;
}

function TableRows({ items }: { items: ItemProps[] }) {
  return (
    <React.Fragment>
      {items.map(item => (
        <React.Fragment key={item.id}>
          <td>{item.id}</td>
          <td>{item.name}</td>
        </React.Fragment>
      ))}
    </React.Fragment>
  );
}

// Usage example within a table body:
// <tbody>
//   <TableRows items={[{ id: 1, name: 'Apple' }, { id: 2, name: 'Banana' }]} />
// </tbody>
// This will render directly:
// <tbody>
//   <td>1</td><td>Apple</td>
//   <td>2</td><td>Banana</td>
// </tbody>
// (Note: This is a simplified example; typically, each row would be a <tr>, but this shows Fragment with keys.)

import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  const increment = () => {
    setCount(count + 1);
  };

  // When setCount is called:
  // 1. React creates a new VDOM: <p>Count: 1</p>
  // 2. Diffs it with old VDOM: <p>Count: 0</p>
  // 3. Identifies only the text content within the <p> tag changed.
  // 4. Updates ONLY that text content in the real DOM.
  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
    </div>
  );
}

// Imagine the real DOM initially:
// <div>
//   <p>Count: 0</p>
//   <button>Increment</button>
// </div>

import React, { useState } from 'react';

function ItemList() {
  const initialItems = [
    { id: 'a', text: 'First' },
    { id: 'b', text: 'Second' },
    { id: 'c', text: 'Third' },
  ];
  const [items, setItems] = useState(initialItems);

  const shuffleItems = () => {
    setItems([...items].sort(() => Math.random() - 0.5));
  };

  const addItemAtBeginning = () => {
    const newItemId = Math.random().toString(36).substring(7);
    setItems([{ id: newItemId, text: 'New Item' }, ...items]);
  };

  return (
    <div>
      <button onClick={shuffleItems}>Shuffle Items</button>
      <button onClick={addItemAtBeginning}>Add Item at Beginning</button>
      <ul>
        {items.map(item => (
          <li key={item.id}>{item.text}</li> // Good: Using stable, unique ID as key
          // <li key={index}>{item.text}</li> // Bad: Using index as key for dynamic list
        ))}
      </ul>
    </div>
  );
}

