import React, { useState, useCallback, memo } from 'react';

const ListItem = memo(({ index, value }) => {
  console.log(`Rendering ListItem ${index}`);
  return (
    <div style={{ padding: '2px 0' }}>
      Item {index}: {value}
    </div>
  );
});

function LargeListApp() {
  const initialItems = Array.from({ length: 10000 }, (_, i) => ({
    id: i,
    value: Math.floor(Math.random() * 1000)
  }));

  const [items, setItems] = useState(initialItems);

  const updateRandomItems = useCallback(() => {
    const itemsToUpdate = new Set();
    while (itemsToUpdate.size < 10) {
      itemsToUpdate.add(Math.floor(Math.random() * items.length));
    }

    const newItems = items.map((item, index) => {
      if (itemsToUpdate.has(index)) {
        return { ...item, value: Math.floor(Math.random() * 1000) };
      }
      return item;
    });
    setItems(newItems);
  }, [items]);

  return (
    <div>
      <h1>Large List Reconciliation Demo</h1>
      <button onClick={updateRandomItems}>Update Random 10 Items</button>
      <div style={{ height: '400px', overflowY: 'scroll', border: '1px solid #ccc' }}>
        {items.map(item => (
          <ListItem key={item.id} index={item.id} value={item.value} />
        ))}
      </div>
    </div>
  );
}

