import React, { useState, useEffect } from 'react';

// A Higher-Order Component (HOC) to inject user data
const withUser = (WrappedComponent) => {
  const WithUserComponent = (props) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
      // Simulate fetching user data
      setTimeout(() => {
        const fetchedUser = { name: 'John Doe', id: 1 };
        setUser(fetchedUser);
        setLoading(false);
      }, 1000);
    }, []);

    if (loading) {
      return <div>Loading user data...</div>;
    }

    return <WrappedComponent {...props} user={user} />;
  };
  WithUserComponent.displayName = `WithUser(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return WithUserComponent;
};

// Original Component to be wrapped
const UserProfile = ({ user, greeting }) => (
  user ? <div>{greeting}, {user.name}!</div> : <div>Loading...</div>
);

// Applying the HOC
