import React, { useState, useEffect } from 'react';

// Component using 'children' as a render prop
function DataLoader({ url, children }) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Simulate API call
        const response = await new Promise(resolve => setTimeout(() => {
          if (url === '/api/users') resolve({ name: 'Jane Doe', email: 'jane@example.com' });
          else if (url === '/api/products') resolve([{ id: 1, name: 'Laptop' }, { id: 2, name: 'Keyboard' }]);
          else throw new Error('Not Found');
        }, 500));
        setData(response);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [url]);

  // 'children' is called as a function here
  return children({ data, loading, error });
}

// Usage
function UserDisplay() {
  return (
    <DataLoader url="/api/users">
      {({ data, loading, error }) => {
        if (loading) return <div>Loading user...</div>;
        if (error) return <div>Error: {error.message}</div>;
        if (!data) return <div>No user data.</div>;
        return (
          <div>
            <h2>User Profile</h2>
            <p>Name: {data.name}</p>
            <p>Email: {data.email}</p>
          </div>
        );
      }}
    </DataLoader>
  );
}

function ProductList() {
    return (
      <DataLoader url="/api/products">
        {({ data, loading, error }) => {
          if (loading) return <div>Loading products...</div>;
          if (error) return <div>Error: {error.message}</div>;
          if (!data || data.length === 0) return <div>No products.</div>;
          return (
            <div>
              <h2>Product List</h2>
              <ul>
                {data.map(product => (
                  <li key={product.id}>{product.name}</li>
                ))}
              </ul>
            </div>
          );
        }}
      </DataLoader>
    );
  }

function App() {
    return (
        <div>
            <UserDisplay />
            <hr/>
            <ProductList />
        </div>
    )
}

