  WithLoadingIndicator.displayName = `WithLoadingIndicator(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return WithLoadingIndicator;
};

// Example Usage:
const MyComponent = ({ data }) => <div>Data: {data}</div>;
const MyComponentWithLoading = withLoadingIndicator(MyComponent);

function App() {
  return (
    <div>
      <MyComponentWithLoading loading={true} />
      <MyComponentWithLoading loading={false} data="Hello World" />
    </div>
  );
}
```

