import React, { useState, useEffect } from 'react';

// 1. Implement withAuth HOC here
const withAuth = (WrappedComponent) => {
  const WithAuthComponent = ({ isAuthenticated, ...restProps }) => {
    if (!isAuthenticated) {
      return <div>Please log in to view this content.</div>;
    }
    return <WrappedComponent {...restProps} />;
  };
  WithAuthComponent.displayName = `withAuth(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return WithAuthComponent;
};

// 2. Implement withLogger HOC here
const withLogger = (WrappedComponent) => {
  const WithLoggerComponent = (props) => {
    useEffect(() => {
      const componentName = WrappedComponent.displayName || WrappedComponent.name || 'Component';
      console.log(`${componentName} mounted.`);
      return () => {
        console.log(`${componentName} unmounted.`);
      };
    }, []);

    return <WrappedComponent {...props} />;
  };
  WithLoggerComponent.displayName = `withLogger(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return WithLoggerComponent;
};

// Simple Dashboard component
