import React, { useState } from 'react';

// 1. Implement ThemeContext component here
function ThemeContext({ children }) {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // children is the render prop function
  return children({ theme, toggleTheme });
}

// 2. Implement ThemeDisplay component here
function ThemeDisplay({ theme }) {
  const displayStyle = {
    padding: '10px',
    border: '1px solid',
    borderColor: theme === 'light' ? '#ccc' : '#666',
    background: theme === 'light' ? '#f0f0f0' : '#444',
    color: theme === 'light' ? '#333' : '#eee',
    marginBottom: '10px'
  };

  return (
    <div style={displayStyle}>
      <h2>Current Theme: {theme.charAt(0).toUpperCase() + theme.slice(1)}</h2>
      <p>This component's style reflects the current theme.</p>
    </div>
  );
}

// 3. Implement ThemeToggleButton component here
function ThemeToggleButton({ toggleTheme }) {
  return (
    <button 
      onClick={toggleTheme} 
      style={{
        padding: '10px 20px',
        fontSize: '16px',
        cursor: 'pointer',
        background: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '5px'
      }}
    >
      Toggle Theme
    </button>
  );
}

function App() {
  return (
    <ThemeContext>
      {({ theme, toggleTheme }) => (
        <div style={{
          background: theme === 'light' ? '#fff' : '#222',
          color: theme === 'light' ? '#333' : '#fff',
          minHeight: '100vh',
          padding: '20px',
          fontFamily: 'Arial, sans-serif'
        }}>
          <h1>Theme Switcher Demo</h1>
          <ThemeDisplay theme={theme} />
          <ThemeToggleButton toggleTheme={toggleTheme} />
          <p style={{ marginTop: '20px' }}>
            The background and text color of this page change with the theme.
          </p>
        </div>
      )}
    </ThemeContext>
  );
}

