import React, { createContext, useContext, useState } from 'react';

// Create context for the Tabs component
const TabsContext = createContext(null);

// Main Tabs component that manages state for all sub-components
function Tabs({ children, defaultActiveIndex = 0 }) {
  const [activeIndex, setActiveIndex] = useState(defaultActiveIndex);

  // Provide shared state to all children via context
  return (
    <TabsContext.Provider value={{ activeIndex, setActiveIndex }}>
      <div className="tabs">{children}</div>
    </TabsContext.Provider>
  );
}

// TabList component - container for Tab components
Tabs.TabList = function TabList({ children }) {
  return <div className="tab-list">{children}</div>;
};

// Individual Tab component
Tabs.Tab = function Tab({ children, index }) {
  const { activeIndex, setActiveIndex } = useContext(TabsContext);
  const isActive = activeIndex === index;

  return (
    <div 
      className={`tab ${isActive ? 'active' : ''}`}
      onClick={() => setActiveIndex(index)}
      role="tab"
      aria-selected={isActive}
      tabIndex={isActive ? 0 : -1}
    >
      {children}
    </div>
  );
};

// TabPanels component - container for TabPanel components
Tabs.TabPanels = function TabPanels({ children }) {
  return <div className="tab-panels">{children}</div>;
};

// Individual TabPanel component
Tabs.TabPanel = function TabPanel({ children, index }) {
  const { activeIndex } = useContext(TabsContext);
  const isActive = activeIndex === index;

  if (!isActive) return null; // Only render the active panel

  return (
    <div className="tab-panel" role="tabpanel" hidden={!isActive}>
      {children}
    </div>
  );
};

// Example Usage:
function App() {
  return (
    <Tabs defaultActiveIndex={0}>
      <Tabs.TabList>
        <Tabs.Tab index={0}>First Tab</Tabs.Tab>
        <Tabs.Tab index={1}>Second Tab</Tabs.Tab>
        <Tabs.Tab index={2}>Third Tab</Tabs.Tab>
      </Tabs.TabList>
      <Tabs.TabPanels>
        <Tabs.TabPanel index={0}>Content for the first tab.</Tabs.TabPanel>
        <Tabs.TabPanel index={1}>Content for the second tab.</Tabs.TabPanel>
        <Tabs.TabPanel index={2}>Content for the third tab.</Tabs.TabPanel>
      </Tabs.TabPanels>
    </Tabs>
  );
}

