import React, { useState, useEffect } from 'react';

function DataFetcher({ url, children }) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      setData(null); // Clear previous data

      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    if (url) {
      fetchData();
    } else {
      // Handle case where URL might be initially empty or null
      setLoading(false);
      setError(new Error('URL not provided'));
    }

  }, [url]); // Re-run effect if URL changes

  // children is expected to be a function that receives { data, loading, error }
  return children({ data, loading, error });
}

// Example Usage:
function App() {
  return (
    <div>
      <h1>Data Fetcher Example</h1>
      <DataFetcher url="https://jsonplaceholder.typicode.com/posts/1">
        {({ data, loading, error }) => {
          if (loading) return <p>Loading data...</p>;
          if (error) return <p style={{ color: 'red' }}>Error: {error.message}</p>;
          if (data) return (
            <div>
              <h2>Post Title: {data.title}</h2>
              <p>{data.body}</p>
            </div>
          );
          return <p>No data yet.</p>;
        }}
      </DataFetcher>

      <hr />

      <h2>Another Data Fetcher (simulated invalid URL)</h2>
      <DataFetcher url="https://jsonplaceholder.typicode.com/invalid-path">
        {({ data, loading, error }) => {
          if (loading) return <p>Loading another...</p>;
          if (error) return <p style={{ color: 'red' }}>Error fetching: {error.message}</p>;
          return <p>Data received (if any): {JSON.stringify(data)}</p>;
        }}
      </DataFetcher>

      <hr />

      <h2>Data Fetcher with no URL (should show error)</h2>
      <DataFetcher url="">
        {({ data, loading, error }) => {
          if (loading) return <p>Still loading, even without URL?</p>;
          if (error) return <p style={{ color: 'red' }}>Error: {error.message}</p>;
          return <p>Should not get here.</p>;
        }}
      </DataFetcher>
    </div>
  );
}

