import React, { createContext, useContext, useState, useRef } from 'react';

// Create AccordionContext
const AccordionContext = createContext(null);

// Main Accordion component
function Accordion({ children, defaultIndex = -1 }) {
  const [activeIndex, setActiveIndex] = useState(defaultIndex);

  const contextValue = {
    activeIndex,
    setActiveIndex,
  };

  return (
    <AccordionContext.Provider value={contextValue}>
      <div className="accordion">{children}</div>
    </AccordionContext.Provider>
  );
}

// Accordion.Item component
Accordion.Item = function AccordionItem({ children, index }) {
  // Pass the item's index down to its children (Header/Panel)
  // This allows Header/Panel to know which item they belong to
  const itemContext = useRef({ index }).current;

  return (
    <div className="accordion-item">
      <AccordionItemContext.Provider value={itemContext}>
        {children}
      </AccordionItemContext.Provider>
    </div>
  );
};

// New context for Accordion.Item to pass its index to Header/Panel
const AccordionItemContext = createContext(null);

// Accordion.Header component
Accordion.Header = function AccordionHeader({ children }) {
  const { activeIndex, setActiveIndex } = useContext(AccordionContext);
  const { index: myIndex } = useContext(AccordionItemContext);

  const isActive = activeIndex === myIndex;

  const handleClick = () => {
    setActiveIndex(isActive ? -1 : myIndex); // Toggle logic
  };

  return (
    <h2 
      className={`accordion-header ${isActive ? 'active' : ''}`}
      onClick={handleClick}
      role="button"
      aria-expanded={isActive}
      tabIndex={0}
    >
      {children}
      <span className="accordion-icon">{isActive ? '' : ''}</span>
    </h2>
  );
};

// Accordion.Panel component
Accordion.Panel = function AccordionPanel({ children }) {
  const { activeIndex } = useContext(AccordionContext);
  const { index: myIndex } = useContext(AccordionItemContext);

  const isActive = activeIndex === myIndex;

  if (!isActive) return null; // Hide if not active

  return (
    <div className="accordion-panel" role="region" hidden={!isActive}>
      {children}
    </div>
  );
};

// Example Usage:
function App() {
  return (
    <div style={{ maxWidth: '600px', margin: '20px auto', border: '1px solid #ccc', borderRadius: '5px' }}>
      <h1 style={{ textAlign: 'center' }}>Simple Accordion</h1>
      <Accordion defaultIndex={0}>
        <Accordion.Item index={0}>
          <Accordion.Header>Section One - What is React?</Accordion.Header>
          <Accordion.Panel>
            <p>React is a JavaScript library for building user interfaces. It lets you compose complex UIs from small and isolated pieces of code called 'components'.</p>
            <p>React's core principle is declarative programming, making your code more predictable and easier to debug.</p>
          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item index={1}>
          <Accordion.Header>Section Two - Why use Hooks?</Accordion.Header>
          <Accordion.Panel>
            <p>Hooks let you use state and other React features without writing a class. They make components easier to read, test, and compose.</p>
            <p>Popular hooks include <code>useState</code>, <code>useEffect</code>, <code>useContext</code>, and <code>useCallback</code>.</p>
          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item index={2}>
          <Accordion.Header>Section Three - Compound Components Benefit</Accordion.Header>
          <Accordion.Panel>
            <p>Compound components provide a flexible and declarative API, allowing users to arrange sub-components freely while sharing implicit state via React Context. This avoids prop drilling and enhances reusability.</p>
            <button style={{ padding: '8px 15px', background: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>Learn More</button>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </div>
  );
}

