import React, { useState, useEffect } from 'react';

interface User {
  id: number;
  name: string;
  email: string;
}

interface SearchableTableProps {
  data: User[];
}

const initialUsers: User[] = [
  { id: 1, name: 'Alice Smith', email: 'alice@example.com' },
  { id: 2, name: 'Bob Johnson', email: 'bob@example.com' },
  { id: 3, name: 'Charlie Brown', email: 'charlie@example.com' },
  { id: 4, name: 'David Lee', email: 'david@example.com' },
  { id: 5, name: 'Eve Davis', email: 'eve@example.com' },
  { id: 6, name: 'Frank White', email: 'frank@example.com' },
  { id: 7, name: 'Grace Green', email: 'grace@example.com' },
  { id: 8, name: 'Harry Black', email: 'harry@example.com' },
  { id: 9, name: 'Ivy Blue', email: 'ivy@example.com' },
  { id: 10, name: 'Jack Red', email: 'jack@example.com' },
];

const DEBOUNCE_DELAY = 300; // milliseconds

export function SearchableTable({ data }: SearchableTableProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [filteredData, setFilteredData] = useState<User[]>(data);

  // Effect for debouncing searchTerm
  useEffect(() => {
    if (searchTerm !== '') {
        setIsSearching(true); // Indicate searching as soon as input changes
    }

    const handler = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, DEBOUNCE_DELAY);

    // Cleanup function to clear the timeout if searchTerm changes before delay
    return () => {
      clearTimeout(handler);
    };
  }, [searchTerm]);

  // Effect for filtering data based on debouncedSearchTerm
  useEffect(() => {
    const lowercasedDebouncedTerm = debouncedSearchTerm.toLowerCase();
    const newFilteredData = data.filter(user =>
      user.name.toLowerCase().includes(lowercasedDebouncedTerm) ||
      user.email.toLowerCase().includes(lowercasedDebouncedTerm)
    );
    setFilteredData(newFilteredData);
    setIsSearching(false); // Filtering complete
  }, [debouncedSearchTerm, data]);

  return (
    <div>
      <input
        type="text"
        placeholder="Search by name or email..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ width: '300px', padding: '8px', marginBottom: '15px' }}
      />
      {isSearching && <p>Searching...</p>}
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ background: '#f2f2f2' }}>
            <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>ID</th>
            <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Name</th>
            <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Email</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.length === 0 && !isSearching && debouncedSearchTerm !== '' ? (
            <tr>
              <td colSpan={3} style={{ textAlign: 'center', padding: '10px' }}>No users found for "{debouncedSearchTerm}".</td>
            </tr>
          ) : filteredData.length === 0 && debouncedSearchTerm === '' ? (
            <tr>
              <td colSpan={3} style={{ textAlign: 'center', padding: '10px' }}>Start typing to search users.</td>
            </tr>
          ) : (
            filteredData.map(user => (
              <tr key={user.id}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.id}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.name}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.email}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

