import React from 'react';

class SimpleCounter extends React.Component {
  state = {
    count: 0
  };

  handleIncrement = () => {
    this.setState(prevState => ({
      count: prevState.count + 1
    }));
  };

  handleDecrement = () => {
    this.setState(prevState => ({
      count: prevState.count - 1
    }));
  };

  render() {
    return (
      <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', margin: '20px' }}>
        <h2>Counter: {this.state.count}</h2>
        <button onClick={this.handleIncrement} style={{ margin: '5px' }}>Increment</button>
        <button onClick={this.handleDecrement} style={{ margin: '5px' }}>Decrement</button>
