import React from 'react';

class ToggleContent extends React.Component {
  state = {
    isVisible: false
  };

  handleToggle = () => {
    this.setState(prevState => ({
      isVisible: !prevState.isVisible
    }));
  };

  render() {
    return (
      <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '5px', margin: '20px' }}>
        <button onClick={this.handleToggle}>
          {this.state.isVisible ? 'Hide Content' : 'Show Content'}
        </button>

        {this.state.isVisible && (
          <div style={{ marginTop: '15px', padding: '10px', backgroundColor: '#f9f9f9', border: '1px dashed #eee' }}>
            <p>This is the hidden content!</p>
            <p>It becomes visible when the button is clicked.</p>
          </div>
        )}
      </div>
    );
  }
}

