import React from 'react';

class Counter extends React.Component {
  state = {
    count: 0
  };

  increment = () => {
    // Basic update: new state does not depend on old state, or simple increment
    this.setState({ count: this.state.count + 1 });
  };

  render() {
    return (
      <div>
        <p>Count: {this.state.count}</p>
        <button onClick={this.increment}>Increment</button>
      </div>
    );
  }
}

import React from 'react';

class DataFetcher extends React.Component {
  state = {
    data: null,
    loading: false
  };

  fetchData = () => {
    this.setState({ loading: true }, () => {
      // This callback runs AFTER loading state is updated and component potentially re-rendered
      console.log('Loading state updated to true. Now fetching data...');
      // Simulate API call
      setTimeout(() => {
        this.setState({ data: 'Fetched Data!', loading: false });
      }, 1000);
    });
  };

  render() {
    return (
      <div>
        <button onClick={this.fetchData} disabled={this.state.loading}>
          {this.state.loading ? 'Loading...' : 'Fetch Data'}
        </button>
        {this.state.data && <p>{this.state.data}</p>}
      </div>
    );
  }
}

import React from 'react';

class Counter extends React.Component {
  state = { count: 0 };

  incrementSafely = () => {
    // Functional update: prevState ensures we always use the latest count value
    this.setState(prevState => ({
      count: prevState.count + 1
    }));
  };

  // Batch multiple state updates
  resetAndIncrement = () => {
    // Even though both are setState, React might batch them. Using functional update
    // for the second call ensures it operates on the state after the first update.
    this.setState({ count: 0 }); // This might be batched with the next update
    this.setState(prevState => ({ count: prevState.count + 1 }));
  };

  render() {
    return (
      <div>
        <p>Count: {this.state.count}</p>
        <button onClick={this.incrementSafely}>Increment Safely</button>
        <button onClick={this.resetAndIncrement}>Reset and Increment</button>
      </div>
    );
  }
}

import React from 'react';

class UserForm extends React.Component {
  state = {
    user: {
      name: '',
      email: '',
      preferences: {
        newsletter: false,
        notifications: true
      }
    }
  };

  handleNameChange = (event) => {
    // Correctly updating a top-level nested property (name)
    this.setState({
      user: {
        ...this.state.user, // Copy existing user properties
        name: event.target.value // Update 'name'
      }
    });
  };

  toggleNewsletter = () => {
    // Correctly updating a deeply nested property (newsletter) using functional update
    this.setState(prevState => ({
      user: {
        ...prevState.user, // Copy existing user properties
        preferences: {
          ...prevState.user.preferences, // Copy existing preferences properties
          newsletter: !prevState.user.preferences.newsletter // Toggle 'newsletter'
        }
      }
    }));
  };

  render() {
    const { user } = this.state;
    return (
      <div>
        <label>Name:</label>
        <input type="text" value={user.name} onChange={this.handleNameChange} />
        <p>Newsletter: {user.preferences.newsletter ? 'Yes' : 'No'}</p>
        <button onClick={this.toggleNewsletter}>Toggle Newsletter</button>
      </div>
    );
  }
}

import React, { useState } from 'react';

function Counter() {
  // Declare a state variable 'count' initialized to 0, and its updater 'setCount'
  const [count, setCount] = useState(0);

  const increment = () => {
    // Direct update: new state does not depend on old state, or simple increment
    setCount(count + 1);
  };

  const decrement = () => {
    // Direct update
    setCount(count - 1);
  };

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

import React, { useState } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  const incrementSafely = () => {
    // Functional update: prevCount is guaranteed to be the latest state
    setCount(prevCount => prevCount + 1);
  };

  const resetAndIncrement = () => {
    // Example of batched updates using functional form for reliability
    setCount(0); // This sets count to 0, but might be batched
    setCount(prevCount => prevCount + 1); // This ensures increment happens after 0, regardless of batching
  };

  return (
    <div>
      <p>Count: {count}</p>
      <button onClick={incrementSafely}>Increment Safely</button>
      <button onClick={resetAndIncrement}>Reset and Increment</button>
    </div>
  );
}

import React, { useState } from 'react';

function UserForm() {
  const [user, setUser] = useState({
    name: '',
    email: '',
    preferences: {
      newsletter: false,
      notifications: true
    }
  });

  const handleNameChange = (event) => {
    // Correctly updating a top-level nested property (name)
    setUser(prevUser => ({
      ...prevUser, // Copy existing user properties
      name: event.target.value // Update 'name'
    }));
  };

  const toggleNewsletter = () => {
    // Correctly updating a deeply nested property (newsletter) using functional update
    setUser(prevUser => ({
      ...prevUser, // Copy existing user properties
      preferences: {
        ...prevUser.preferences, // Copy existing preferences properties
        newsletter: !prevUser.preferences.newsletter // Toggle 'newsletter'
      }
    }));
  };

  const handleEmailChange = (event) => {
    // Example: Direct update (less safe if batched, but common for independent updates)
    setUser({
      ...user, // Needs 'user' from the current render scope, which might be stale
      email: event.target.value
    });
  };

  return (
    <div>
      <label>Name:</label>
      <input type="text" value={user.name} onChange={handleNameChange} />
      <label>Email:</label>
      <input type="email" value={user.email} onChange={handleEmailChange} />
      <p>Newsletter: {user.preferences.newsletter ? 'Yes' : 'No'}</p>
      <button onClick={toggleNewsletter}>Toggle Newsletter</button>
    </div>
  );
}

import React, { useState } from 'react';

function TodoList() {
  const [todos, setTodos] = useState([
    { id: 1, text: 'Learn React', completed: false },
    { id: 2, text: 'Build a project', completed: true }
  ]);

  const addTodo = (text) => {
    const newTodo = { id: Date.now(), text, completed: false };
    setTodos(prevTodos => [...prevTodos, newTodo]); // Add new item
  };

  const toggleTodo = (id) => {
    setTodos(prevTodos =>
      prevTodos.map(todo =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  return (
    <div>
      <input type="text" onKeyPress={(e) => { if (e.key === 'Enter') { addTodo(e.target.value); e.target.value = ''; } }} placeholder="Add new todo" />
      <ul>
        {todos.map(todo => (
          <li key={todo.id} style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
            <span onClick={() => toggleTodo(todo.id)}>{todo.text}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

